mapper.select:
SELECT
c_invoice_id, ad_client_id, ad_org_id, isactive, created, createdby, updated, updatedby, issotrx, documentno, docstatus, docaction, processing, processed, posted, c_doctype_id, c_doctypetarget_id, c_order_id, description, isapproved, istransferred, isprinted, salesrep_id, dateinvoiced, dateprinted, dateacct, c_bpartner_id, c_bpartner_location_id, poreference, isdiscountprinted, dateordered, c_currency_id, paymentrule, c_paymentterm_id, c_charge_id, chargeamt, totallines, grandtotal, m_pricelist_id, istaxincluded, c_campaign_id, c_project_id, c_activity_id, ispaid, c_payment_id, c_cashline_id, createfrom, generateto, sendemail, ad_user_id, copyfrom, isselfservice, ad_orgtrx_id, user1_id, user2_id, c_conversiontype_id, ispayschedulevalid, ref_invoice_id, isindispute, invoicecollectiontype, m_rma_id, dunninggrace, c_dunninglevel_id, reversal_id, processedon, isfixedassetinvoice, c_pos_id, user3_id, user4_id, uuid FROM "adempiere".c_invoice
AND DocumentNo = #{documentNo, jdbcType = VARCHAR}
AND c_order_id = #{orderId, jdbcType = INTEGER}
AND AD_Client_ID = #{client, jdbcType = VARCHAR}

mapper.update:
update "adempiere".c_invoice set
c_order_ido = #{orderId},
AD_User_ID = #{userOrContact},
M_PriceList_ID = #{priceList},
IsDiscountPrinted = #{discountPrinted},
TotalLines = #{totalLines}
GrandTotal = #{grandTotal}
IsPayScheduleValid = #{payScheduleValid}
Posted = #{posted}
paid = #{paid}
where c_invoice_id = #{invoiceId}
